import React from "react";

function convertMS(value) {
    const sec = parseInt(value, 10); // convert value to number if it's string
    let minutes = Math.floor((sec/60)); // get minutes
    let seconds = sec - (minutes * 60); //  get seconds
    // add 0 if value < 10; Example: 2 => 02
    if (minutes < 10) {minutes = "0"+minutes;}
    if (seconds < 10) {seconds = "0"+seconds;}
    return minutes+':'+seconds; // Return is MM : SS
  }

  

function TimerBar({session, breakDuration, focusDuration}) {
    //formats Progress Bar
    function progressbarStyle(focusDuration, breakDuration, session){
        if (session?.label === "Focusing") {
          const amt= session.timeRemaining / focusDuration * 100;
          return amt;
        } else {
          const amt= session.timeRemaining / breakDuration * 100;
          return amt;
        }
      }
    
    if (session !== null) {
      return (
        <div>
          <div className="row mb-2">
              <div className="col">
                {/* Session & Total Session Time*/}
                <h2 data-testid="session-title">
                  {session?.label} for 
                  <span> {session?.label === "Focusing" ? convertMS(focusDuration) : convertMS(breakDuration)} </span>
                  minutes
                </h2>
                {/* Countdown Timer */}
                <p className="lead" data-testid="session-sub-title">
                  {convertMS(session?.timeRemaining)} remaining
                </p>
              </div>
            </div>
            <div className="row mb-2">
              <div className="col">
                <div className="progress" style={{ height: "20px" }}>
                  <div
                    className="progress-bar"
                    role="progressbar"
                    aria-valuemin="0"
                    aria-valuemax="100"
                    aria-valuenow={progressbarStyle(focusDuration, breakDuration, session)} // TODO: Increase aria-valuenow as elapsed time increases
                    style={{ width: `${100 - progressbarStyle(focusDuration, breakDuration, session)}%` }} // TODO: Increase width % as elapsed time increases
                  />
                </div>
              </div>
            </div>
          </div>
      )} else {
        return null;
      }
  }

  export default TimerBar;
