import React from "react";

function progressBarStyle({focusDuration, breakDuration, timeRemaining, label}){
  if (label === "Focusing") {
      const amt= timeRemaining / focusDuration * 100;
      return(
        <div>
          className="progress-bar"
          role="progressbar"
          aria-valuemin="0"
          aria-valuemax="100"
          aria-valuenow={amt} 
          style={{ width: `${100 - amt}%` }}
        </div>
        )
    } else {
      const amt= timeRemaining / breakDuration * 100;
      return(
        <div>
          className="progress-bar"
          role="progressbar"
          aria-valuemin="0"
          aria-valuemax="100"
          aria-valuenow={amt}
          style={{ width: `${100 - amt}%` }}
        </div>
        )
    }
}

export default progressBarStyle;
