import React from "react";

function ProgressBarStyle({focusDuration, breakDuration, timeRemaining, label}){
  let amt;
  if (label === "Focusing") {
      amt= timeRemaining / focusDuration * 100;
    } else {
      amt = timeRemaining / breakDuration * 100;
    }
    //style={{ width: `${100 - amt}%` }}
    return(
        <div className="row mb-2">
          <div className="col">
            <div className="progress" style={{ height: "20px" }}>
              <div>
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow={amt}
                
              </div>
            </div>
          </div>
        </div>
      )
}

export default ProgressBarStyle;
